import Callable from "./utils/Callable"
import LightqueryCollection from "./LightqueryCollection"
import InvalidArgumentError from "./errors/InvalidArgumentError"
import { asSequence, registerLightqueryExtensions } from "./utils/lazy"


/**
 * Class that represents the factory function to query the DOM with lightquery
 */
export default class LightqueryFactory extends Callable{
	/**
	 * Create a lightquery factory (that's what's behind µ)
	 * @param   {typeof LightqueryCollection} [collectionClass = LightqueryCollection] The class used to construct the result collections
	 * @param   {boolean} [strictMode = true] Whether or not to throw exceptions instead of silently failing
	 */
	constructor(collectionClass = LightqueryCollection, strictMode = true){
		super();
		registerLightqueryExtensions(this);
		collectionClass.lightquery = this;
		
		/**
		 * Private methods and properties
		 * @protected
		 * @readonly
		 * @memberof LightqueryFactory
		 */
		this.__ = {
			/**
			 * Create a lightquery collection from arguments
			 * @param {any[]} args The arguments for the constructor
			 * @returns {LightqueryCollection}
			 */
			factory: (...args) => new collectionClass(...args),
			
			/**
			 * @property {typeof LightqueryCollection} The class used to generate a results collection
			 */
			collectionClass,
			
			/**
			 * A factory for an empty selection
			 * @returns {LightqueryCollection}
			 */
			emptySelection(){
				return this.factory("");
			},
		};
		
		/**
		 * @member {boolean} Whether or not to use strict mode with this factory
		 */
		this.strictMode = strictMode;
	}
	
	/**
	 * @override
	 * @inheritdoc
	 */
	__call(selector, context = undefined, previousResults = []){
		if(typeof selector === "function")
			return this.ready(selector);
		else	
			return this.__.factory(selector);
	}
	
	/**
	 * Execute a callback once the DOM is fully loaded
	 * @param   {Function}             callback The function to execute after the DOM is loaded
	 * @returns {LightqueryCollection}
	 */
	ready(callback){
		return this(document).ready(callback);
	}
	
	/**
	 * Wrap an iterable in a lazy evaluated pipeline
	 * @template T
	 * @param   {Iterable<T>} iterable The iterable to wrap
	 * @returns {Sequence<T>}
	 */
	lazy(iterable){
		return asSequence(iterable);
	}
	
	/**
	 * Help query using the given context as the selection root 
	 * @param   {Element} context The context to restrict to
	 * @returns {{select: (selector: string) => LightqueryCollection}}
	 */
	from(context){
		return {
			select: selector => this(selector, context),
		};
	}
}

import UnimplementedError from "../../errors/UnimplementedError"

export default class AbstractStrategy{
	/**
	 * Determine whether or not it should process the given selector
	 * @param {any}               selector        The selector to process
 	 * @param {Element|undefined} context         The context to get elements from
 	 * @param {Iterable<Element>} previousResults The previous set of results
	 * @returns {boolean}
	 */
	shouldProcess(selector, context = undefined, previousResults = []){
		throw new UnimplementedError("Call to unimplemented AbstractStrategy#shouldProcess(selector)");
	}
	
	/**
	 * Processes the given selector
	 * @param {any}               selector        The selector to process
 	 * @param {Element|undefined} context         The context to get elements from
 	 * @param {Iterable<Element>} previousResults The previous set of results
	 * @returns {Iterable<Element>}
	 */
	process(selector, context = undefined, previousResults = []){
		throw new UnimplementedError("Call to unimplemented AbstractStrategy#process(selector)");
	}
}

import AbstractStrategy from "./AbstractStrategy"
import { cssEngine } from "../../CssEngine"

export default class CssSelectorStrategy extends AbstractStrategy{
	shouldProcess(selector, context = undefined, previousResults = []){
		return typeof selector === "string";
	}
	
	process(selector, context = undefined, previousResults = []){
		return cssEngine.findAll(selector, context, previousResults);
	}
}

import AbstractStrategy from "./AbstractStrategy"
import SingleElementStrategy from "./SingleElementStrategy"
import LightqueryCollection from "../../LightqueryCollection"

export default class IterableStrategy extends AbstractStrategy{
	static get allowedClasses(){
		//TODO: Add sequency sequences to the list
		return [
//			Iterator,
			Array,
			NodeList,
		];
	}
	
	constructor(){
		super();
		this.allowedClasses = IterableStrategy.allowedClasses;
		this.allowedItemClasses = SingleElementStrategy.allowedClasses;
	}
	
	shouldProcess(selector, context = undefined, previousResults = []){
		if(!selector)
			return false;
		
		
		const anyClass = this.allowedClasses.some(clazz => selector instanceof clazz);
		if(!anyClass)
			return false;
		
		//TODO: Check if we really want item consistency
		/*for(const item of selector){
			if(!this.allowedItemClasses.some(clazz => item instanceof clazz))
				return false;
		}*/
		
		return true;
	}
	
	process(selector, context = undefined, previousResults = []){
		return [...previousResults, ...selector];
	}
}

import AbstractStrategy from "./AbstractStrategy"

export default class SingleElementStrategy extends AbstractStrategy{
	static get allowedClasses(){
		return [
			Element,
			Document,
			DocumentFragment,
			ShadowRoot,
			Window,
		];
	}
	
	constructor(){
		super();
		this.allowedClasses = SingleElementStrategy.allowedClasses;
	}
	
	shouldProcess(selector, context = undefined, previousResults = []){
		return selector && this.allowedClasses.some(clazz => selector instanceof clazz);
	}
	
	process(selector, context = undefined, previousResults = []){
		return [...previousResults, selector];
	}
}

